mapper.update:
update sms_acount
brand_id = #{brandId,jdbcType=VARCHAR}, sms_sign = #{smsSign,jdbcType=VARCHAR}, remainder_num = #{remainderNum,jdbcType=INTEGER}, used_num = #{usedNum,jdbcType=INTEGER}, sms_unit_price = #{smsUnitPrice,jdbcType=DECIMAL}, total_amcount = #{totalAmcount,jdbcType=DECIMAL}, used_amcount = #{usedAmcount,jdbcType=DECIMAL}, remainer_amcount = #{remainerAmcount,jdbcType=DECIMAL}, sms_remind = #{smsRemind}, sms_notice_telephone = #{smsNoticeTelephone},
where id = #{id,jdbcType=VARCHAR}

UPDATE sms_acount SET
remainder_num = remainder_num+#{number},total_amcount = total_amcount+#{money},remainer_amcount=remainer_amcount+#{money}
where brand_id = #{brandId}
