mapper.select:
SELECT COUNT(*) FROM tb_article_top WHERE type =5 AND

create_time>#{beginDate} and create_time<#{endDate}
 AND  shop_detail_id= #{shopId}

 SELECT COUNT(*) FROM tb_article_top WHERE type in(1,2,3,4) AND

create_time>#{beginDate} and create_time<#{endDate}
 AND  shop_detail_id = #{shopId}

SELECT name,type,COUNT(*)num FROM tb_article_top WHERE type=5 AND shop_detail_id=#{shopId} AND name !='' AND
create_time>#{beginDate} and create_time<#{endDate}
GROUP BY name ORDER BY num desc LIMIT 10

SELECT name,type,COUNT(*)num FROM tb_article_top WHERE type in(1,2.3,4) AND shop_detail_id=#{shopId} AND name !='' AND
create_time>#{beginDate} and create_time<#{endDate}
GROUP BY name ORDER BY num desc LIMIT 10
