mapper.update:
update tb_coupon
name = #{name,jdbcType=VARCHAR}, value = #{value,jdbcType=DECIMAL}, min_amount = #{minAmount,jdbcType=DECIMAL}, begin_date = #{beginDate,jdbcType=DATE}, end_date = #{endDate,jdbcType=DATE}, begin_time = #{beginTime,jdbcType=TIME}, end_time = #{endTime,jdbcType=TIME}, is_used = #{isUsed,jdbcType=BIT}, using_time = #{usingTime,jdbcType=TIMESTAMP}, coupon_source = #{couponSource,jdbcType=VARCHAR}, use_with_account = #{useWithAccount,jdbcType=BIT}, remark = #{remark,jdbcType=VARCHAR}, distribution_mode_id = #{distributionModeId,jdbcType=INTEGER}, customer_id = #{customerId,jdbcType=VARCHAR}, coupon_type = #{couponType,jdbcType=VARCHAR}, shop_detail_id = #{shopDetailId,jdbcType=VARCHAR}, brand_id = #{brandId,jdbcType=VARCHAR}, small_program_id = #{smallProgramId}, use_shop_id = #{useShopId,jdbcType=VARCHAR}, deduction_type = #{deductionType},
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id,name,value,min_amount,begin_date,end_date,begin_time,end_time,is_used,using_time,
coupon_source,use_with_account,remark,distribution_mode_id,customer_id,shop_detail_id,brand_id
 from tb_coupon where is_used=#{isUsed} and distribution_mode_id=#{distributionModeId} and customer_id=#{customerId}
 AND  brand_id=#{brandId}
 AND begin_date<=current_date AND end_date >=current_date AND begin_time<=NOW() AND end_time>=NOW()
 order by value DESC

select * from tb_coupon
where customer_id = #{customerId}
AND shop_detail_id = #{shopId}

    and is_used= ${status}


    AND ((begin_date<=current_date()  AND end_date >=current_date()) OR begin_date > current_date())


    AND end_date <current_date()

order by `value` DESC

select * from tb_coupon where is_used = 0
and #{time} = TIMESTAMPDIFF(DAY ,DATE_FORMAT(now(),"%y-%m-%d"),DATE_FORMAT(end_date,"%y-%m-%d"))

    and brand_id = #{shopId}


    and shop_detail_id = #{shopId}
