mapper.select:
SELECT FROM tb_get_number WHERE shop_detail_id = #{shopId} and state = 0 and DATE_FORMAT(create_time,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d')
and table_type = #{tableType}
order by create_time asc

select id from tb_get_number where table_type = #{tableType}
and DATE_FORMAT(create_time,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d')
and #{date} >= create_time and shop_detail_id = #{shopId}

select * from tb_get_number where shop_detail_id = #{shopId}
and  #{timeOut}         > TIMESTAMPDIFF(HOUR,create_time,now())
and customer_id = #{customerId} order by create_time desc limit 1

select * from tb_get_number where shop_detail_id = #{shopId} and code_id = #{codeId} and state = 0
and DATE_FORMAT(create_time,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d') and call_number_time is not null
order by call_number_time desc limit 1;
