mapper.select:
select *, CASE WHEN show_times > (SELECT count(1) FROM tb_customer_notice_history WHERE customer_id= #{customerId} and notice_id = tb_notice.id) THEN 0 ELSE 1 END is_read from tb_notice where shop_detail_id = #{shopId}
and notice_type = #{noticeType}
and status=1 order by sort

  	select * from tb_notice
	where shop_detail_id = #{shopId}
	order by sort
