shop_detail_id=#{shopId}
AND
 create_time >#{dateBegin} AND create_time  <#{dateEnd}
and order_state = 1 and production_status != 5
and table_number is not null
order by create_time DESC;

select * from tb_order where customer_id = #{customerId} and shop_detail_id = #{shopId} and ((order_mode = 5 and order_state = 1 and #{time} > now() - create_time) or (order_mode != 5 and order_mode != 6 and #{time} > now() - create_time) or(order_mode = 6 and #{time} > now() - create_time)) and table_number is not null and allow_continue_order = 1 and production_status != 6 and order_state != 9
and table_number = #{tableNumber}
order by create_time desc limit 1

