select tod.serial_number,tod.order_state,tod.production_status,push_order_time, print_order_time,call_number_time,order_money,topi.id orderPaymentId,topi.pay_value,topi.payment_mode_id, toi.article_name,(toi.orgin_count - toi.refund_count) count from tb_order tod inner join tb_order_payment_item topi on tod.id = topi.order_id inner join tb_order_item toi on tod.id = toi.order_id
tod.serial_number = #{serialNumber}
tod.id = #{orderId}

SELECT o.id,o.shop_detail_id,o.create_time,o.order_state,o.order_money,o.production_status,c.id customer_id ,c.telephone, a.id appraise_id, a.level, a.content,oi.id orderPaymentItem_id , oi.payment_mode_id , oi.pay_value FROM tb_customer c INNER JOIN tb_order o ON c.id = o.customer_id LEFT JOIN tb_appraise a on a.order_id = o.id LEFT JOIN tb_order_payment_item oi ON o.id = oi.order_id WHERE c.id =#{customerId}
and o.create_time >= #{ beginDate }
and o.create_time <= #{ endDate }

select count(1) from tb_order where customer_id = #{customerId}
and (parent_order_id is null or parent_order_id = '')
and order_state in (2,10,11) and production_status in (2,3,4)

select count(1) from tb_order
where shop_detail_id = #{shopId}
and table_number = #{tableNumber}
and pay_type = 1 and order_state = 1
and (parent_order_id is null or parent_order_id = '')
and pay_type = 1
and #{closeContinueTime} > TIMESTAMPDIFF(SECOND,create_time,now())
and customer_id != #{customerId}

select * from tb_order where shop_detail_id = #{shopId}
and order_state in (2,10,11,12,13) and production_status in (2,3,4)
and   create_time <= #{endDate} and create_time>=#{beginDate}

order by create_time asc

SELECT o.id, o.order_mode, o.pay_type, o.customer_id, c.share_customer, o.order_money, o.amount_with_children, oi.payment_mode_id FROM tb_order o INNER JOIN tb_order_payment_item oi ON o.id = oi.order_id LEFT JOIN tb_customer c ON o.customer_id = c.id WHERE o.order_state IN (2, 10, 11, 12) AND o.production_status in (2,3,4) AND o.shop_detail_id = #{shopId} AND o.parent_order_id is null
AND o.create_time>#{beginDate} AND o.create_time < #{endDate}
AND o.customer_id NOT IN ( SELECT customer_id FROM tb_order WHERE order_state IN (2, 10, 11, 12) AND production_status in (2,3,4) AND shop_detail_id = #{shopId} AND
create_time<#{beginDate}
)

SELECT o.customer_id, COUNT(*)num FROM tb_order o WHERE o.order_state IN (2, 10, 11, 12) AND o.production_status in (2,3,4) AND
o.create_time <#{beginDate}
AND o.shop_detail_id = #{shopId} AND o.customer_id in ( SELECT customer_id FROM tb_order WHERE order_state in(2,10,11,12) AND production_status in (2,3,4) AND
create_time>#{beginDate} AND create_time<#{endDate}
AND shop_detail_id=#{shopId} ) GROUP BY customer_id

select td.id,td.create_time,td.original_amount,td.order_money,top.id order_payment_id,top.payment_mode_id,top.pay_value from tb_order td left join tb_order_payment_item top on td.id = top.order_id where IF(td.production_status = 6, td.production_status = 6 , td.order_state in (2,10,11,12) and IF(td.is_pos_pay = 1,td.is_pos_pay = 1,td.production_status in (2,3,4))) and td.create_time >= CONCAT(#{beginDate},' 00:00:00') and td.create_time <= CONCAT(#{endDate},' 23:59:59')
and td.shop_detail_id = #{shopId}
ORDER BY td.create_time ASC

SELECT o.id, o.order_mode, o.pay_type, o.customer_id, c.share_customer, o.order_money, o.amount_with_children, oi.payment_mode_id FROM tb_order o INNER JOIN tb_order_payment_item oi ON o.id = oi.order_id LEFT JOIN tb_customer c ON o.customer_id = c.id WHERE o.order_state IN (2, 10, 11, 12) AND o.production_status in (2,3,4) AND o.brand_id = #{brandId}
AND o.create_time>#{beginDate} AND o.create_time < #{endDate}
AND o.customer_id NOT IN ( SELECT customer_id FROM tb_order WHERE order_state IN (2, 10, 11, 12) AND production_status in (2,3,4) AND brand_id = #{brandId} AND
create_time<#{beginDate}
)

SELECT o.customer_id, COUNT(*)num FROM tb_order o WHERE o.order_state IN (2, 10, 11, 12) AND o.production_status in (2,3,4) AND
o.create_time <#{beginDate}
AND o.brand_id = #{brandId} AND o.customer_id in ( SELECT customer_id FROM tb_order WHERE order_state in(2,10,11,12) AND production_status in (2,3,4) AND
create_time>#{beginDate} AND create_time<#{endDate}
AND brand_id=#{brandId} ) GROUP BY customer_id

