mapper.select:
select id,number,customer_id,article_id,shop_detail_id,distribution_mode_id,shop_type,pid,user_id,attr_id,recommend_id,recommend_article_id,unit_new_id,uuid,group_id,weight_package_id from tb_shop_cart
where
distribution_mode_id =#{distributionModeId} and
article_id = #{articleId} and
customer_id =#{customerId} and
pid = 0 and shop_type = #{shopType} and
shop_detail_id =#{shopDetailId}

    and group_id = #{groupId}


    and uuid = #{uuid}

 limit 1

mapper.update:
update tb_shop_cart
set group_id = null
where id = #{id}

update tb_shop_cart
set group_id = #{groupId}
where shop_detail_id = #{shopId}
and customer_id = #{customerId}

mapper.select:
select * from tb_shop_cart where group_id = #{groupId}

    and customer_id in

        #{item}

mapper.update:
update tb_shop_cart
set group_id = null
where group_id = #{groupId}

update tb_shop_cart
set group_id = #{groupId}
where shop_detail_id = #{shopId}
and customer_id = #{customerId}

update tb_shop_cart
set shop_type = 1
where id = #{id}
