mapper.select:
SELECT o.id, o.charge_money, o.reward_money, o.finish_time, o.shop_detail_id, o.customer_id, IF (o.type IS NULL, 1, o.type) type,
IF ( c.id IS NULL, '没有填写', c.id ) cid,
IF ( c.operation_phone IS NULL, '没有填写', c.operation_phone ) operation_phone,
IF ( o.type = 0,if(c.customer_phone IS NULL,'没有填写',c.customer_phone),(select telephone from tb_customer r where r.id = o.customer_id) ) customer_phone FROM tb_charge_order o LEFT JOIN tb_charge_log c ON c.charge_order_id = o.id WHERE o.shop_detail_id=#{shopDetailId} AND o.order_state = 1 AND o.finish_time is not null
AND o.finish_time>=#{beginDate}
AND o.finish_time <= #{endDate }

select id, charge_money, reward_money, charge_balance, reward_balance, type, finish_time, customer_id from tb_charge_order where order_state = 1
and shop_detail_id = #{shopId}
and finish_time >= CONCAT(#{beginDate},' 00:00:00') and finish_time <= CONCAT(#{endDate},' 23:59:59')

SELECT tcp.charge_order_id orderId, tcp.payment_money payValue, tcp.create_time payTime, tcp.pay_data refundInfo FROM tb_charge_order tco INNER JOIN tb_charge_payment tcp ON tco.id = tcp.charge_order_id WHERE tcp.create_time BETWEEN #{beginDate} AND DATE_ADD(#{endDate}, INTERVAL 1 DAY)
AND tco.shop_detail_id = #{shopId}
ORDER BY tcp.payment_money asc
