mapper.select:
select *, IF((select COUNT(1) from sys_permission sp where sp.parent_id = sn.id and sp.menu_type = 1) > 0,1,0) as isChildLink from sys_permission sn
where user_group_id = #{userGroupId}
order by sort

select *, IF((select COUNT(1) from sys_permission sp where sp.parent_id = sn.id and sp.menu_type = 1) > 0,1,0) as isChildLink from sys_permission sn where (parent_id is null or parent_id = 0)
and user_group_id = #{userGroupId}
order by sort

 	select * from sys_permission where menu_type in 

 		#{menuType}


		and user_group_id = #{userGroupId}

 	order by sort
