mapper.update:
update tb_new_custom_coupon
name = #{name,jdbcType=VARCHAR}, coupon_value = #{couponValue,jdbcType=DECIMAL}, coupon_validay = #{couponValiday,jdbcType=INTEGER}, coupon_number = #{couponNumber,jdbcType=INTEGER}, create_time = #{createTime,jdbcType=TIMESTAMP}, use_with_account = #{useWithAccount,jdbcType=BIT}, coupon_name = #{couponName,jdbcType=VARCHAR}, coupon_min_money = #{couponMinMoney,jdbcType=DECIMAL}, begin_time = #{beginTime,jdbcType=TIME}, end_time = #{endTime,jdbcType=TIME}, is_activty = #{isActivty,jdbcType=BIT}, brand_id = #{brandId,jdbcType=VARCHAR}, time_cons_type = #{distributionModeId,jdbcType=INTEGER}, time_cons_type = #{timeConsType}, begin_date_time = #{beginDateTime}, end_date_time = #{endDateTime}, coupon_type = #{couponType}, is_brand = #{isBrand}, recommend_delay_time = #{recommendDelayTime}, distance_birthday_day = #{distanceBirthdayDay}, real_time_coupon_begin_time = #{realTimeCouponBeginTime}, real_time_coupon_end_time = #{realTimeCouponEndTime}, shop_detail_id = #{shopDetailId}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select * from tb_new_custom_coupon where coupon_type = 4 and (is_brand = 1 or shop_detail_id = #{shopId})
and is_activty = 1 and real_time_coupon_begin_time <= #{nowDate} and real_time_coupon_end_time >= #{nowDate}

    and id not in (${realTimeCouponIds})
